```typescript
import { hold, press } from 'keymash';

// Letters and numbers
press.a, press.z, press['1']

// Special keys
press.enter, press.escape, press.space
press.arrowup, press.arrowdown

// Arrow key aliases (shorthand)
press.up === press.arrowup
press.down === press.arrowdown
press.left === press.arrowleft
press.right === press.arrowright

// Other aliases
press.space === press[' ']

// Function keys
press.F1, press.F12

// Catch-all (matches any key)
press.any
```

```typescript
import { keymash, code, hold } from 'keymash';

const km = keymash();

// Physical WASD controls (work on AZERTY, QWERTZ, etc.)
km.bind(code.KeyW, () => moveForward());
km.bind(code.KeyA, () => moveLeft());
km.bind(code.KeyS, () => moveBackward());
km.bind(code.KeyD, () => moveRight());

// With modifiers
km.bind(hold.shift + code.KeyW, () => sprint());

// Available codes: KeyA-KeyZ, Digit0-Digit9, Space, Enter, Tab,
// ArrowUp/Down/Left/Right, Escape, Backspace, and more
