```typescript
import { keymash, ctrl, press } from 'keymash';

const km = keymash({
  label: 'My App',
  scope: document.getElementById('app'),
  bindings: [
    { combo: ctrl + press.s, handler: () => save() }
  ]
});

// Add more bindings
km.bind(ctrl + press.n, () => newFile());

// Clean up when done
km.destroy();
