import { useKeyState, hold } from 'keymash/react';

function KeyIndicator() {
  const mask = useKeyState();
  const isCtrl = (mask & hold.ctrl) !== 0n;
  return <span>{isCtrl ? 'Ctrl pressed' : 'Ctrl not pressed'}</span>;
}
