import { useKeymashBindings } from 'keymash/react';

function KeyboardShortcutsDialog() {
  const bindings = useKeymashBindings();

  return (
    <ul>
      {bindings.map((b, i) => (
        <li key={i}>
          <kbd>{b.comboText}</kbd> {b.label}
        </li>
      ))}
    </ul>
  );
}
